import google.generativeai as genai
import base64

# configure Gemini
genai.configure(api_key="AIzaSyB_Ti97Stv1ji7pdkBTcI2DNNSOSjEx9qw")

model = genai.GenerativeModel("gemini-1.5-flash")

# your base64 data URI
data_uri = """data:image/jpeg;base64,"""

# strip off the prefix and decode
base64_data = data_uri.split(",")[1]
image_data = base64.b64decode(base64_data)

# send both text + image to Gemini
response = model.generate_content([
    {"text": "Describe this outfit."},
    {
        "inline_data": {
            "mime_type": "image/jpeg",
            "data": image_data
        }
    }
])

print(response.text)
